import React from "react";
import styled from "styled-components";

const AuthTemplateBlock = styled.div`
  position: absolute;
  background: url("../../../public/Login.png");
  left: 0;
  top: 0;
  bottom: 0;
  right: 0;
  display: flex;
  flex-direction: column;
  //justify-content: center;
  align-items: center;
`;

/*검은색 박스*/
const BlackBox = styled.div`
  .logo-area {
    display: block;
    padding-bottom: 2rem;
    text-align: center;
    font-weight: bold;
    letter-spacing: 2px;
  }
  h2 {
    color: white;
  }
  position: absolute;
  left: calc(1313 / 1920 * 100) vw;
  top: calc(1080 / 211 * 100) vw;
  right: 151px;

  padding: 2rem;
  width: 455px;
  height: 669px;
  background: black;
  border-radius: 10px;
  box-sizing: border-box;
`;

const AuthTemplate = ({ children }) => {
  return (
    <AuthTemplateBlock>
      <BlackBox>
        <div className="logo-area"></div>
        {children}
      </BlackBox>
    </AuthTemplateBlock>
  );
};

export default AuthTemplate;
