import React from "react";
import styled from "styled-components";

const UserAlign = styled.div`
  position: absolute;
  background-color: #2c2c2c;

  width: calc(100% - 500px);
  height: 47px; //real size 56
  left: 200px;
  border-radius: 15px;
  margin-bottom: 10px;
  color: white;
  text-align: left;
  font-family: Noto Sans KR;
  font-weight: 900px;
  font-size: 25px;
  line-height: 36px;
  padding-left: 46px;
  padding-top: 9px;

  &:first-child {
    margin-top: 0;
  }
  @media (max-width: 1200px) {
    left: 120px;
    height: 35px;
    font-size: 20px;
    padding-top: 1px;
    padding-left: 20px;
  }

  @media (max-width: 1060px) {
    font-size: 15px;
    width: 100px;
    padding-top: 0px;
  }
  @media (max-width: 890px) {
    left: 70px;
  }
`;

const UserList = () => {
  const Users = ["서건희", "킹남대"];

  const name = Users.map((user) => user);
  return name;
};

export default UserList;
