import React from "react";
import Button from "../common/Button";
import styled from "styled-components";
import { Link } from "react-router-dom";
import Icon from "../../img/Icon.png";

const AuthFormBlock = styled.div`
  h3 {
    margin: 0;
    font-size: 40px;
    color: white;
    margin-bottom: 4rem;
    position: absolute;
    left: 71px;
    top: 148px;
    margin-bottom: 1rem;
  }
`;

const StyledInput = styled.input`
  background-color: black;
  line-height: 50px;
  font-size: 1rem;
  border: none;
  outline: none;
  border: solid white;
  border-radius: 10px;
  text-indent: 1rem;

  margin-left: 1.5rem;
  margin-top: 6rem;
  margin-bottom: 2rem;
  width: 80%;

  &:focus {
    color: gray;
  }
  & + & {
    margin-top: 1rem;
  }
`;

const Area = styled.div`
  margin-top: 20px;
  position: absolute;
  top: 150px;
`;

const ButtonMarginTop = styled(Button)`
  margin-top: 1rem;
  margin-left: 1.5rem;
`;

const IconBox = styled.div`
  background-image: url(${Icon});
  object-position: 100px;
  position: absolute;
  width: 120px;
  height: 120px;
  top: 231px;
`;

const AuthForm = () => {
  return (
    <AuthFormBlock>
      <IconBox />
      <h3>ADMIN</h3>
      <Area>
        <StyledInput
          autoComplete="e-mail"
          name="e-mail"
          placeholder=" EMAIL을 입력해주세요"
        />
        <StyledInput
          autoComplete="password"
          name="password"
          placeholder="비밀번호를 입력해주세요"
          type="password"
        />
        <ButtonMarginTop fullWidth>로그인</ButtonMarginTop>
      </Area>
    </AuthFormBlock>
  );
};

export default AuthForm;
