import React from "react";
import styled from "styled-components";
import Button from "../common/Button";

const WhiteBox = styled.div`
  background-color: white;
  width: calc(100% - 440px);
  height: 840px;
  top: 120px;
  left: 370px;
  position: absolute;
  border: 5px solid #000;
  box-sizing: border-box;
  border-radius: 40px;
`;

const StyledInput = styled.input`
  position: absolute;
  width: calc(100% - 200px);
  height: 51px;
  left: 100px;
  top: 74px;
  background-color: #fff;
  border: 1px solid #000;
  box-sizing: border-box;
  border-radius: 10px;
  @media (max-width: 1000px) {
    font-size: 10px;
    height: 41px;
  }
  @media (max-width: 890px) {
    width: 220px;
  }
`;

const ButtonStyled = styled(Button)`
  position: absolute;
  width: 161px;
  height: 51px;
  font-family: CookieRun;
  top: 74px;
  right: 90px;
  color: #fff;
  text-align: center;
  font-size: 30px;
  font-family: Noto Sans KR;
  font-style: normal;
  font-weight: 900px;
  font-size: 20px;
  line-height: 29px;

  @media (max-width: 1000px) {
    width: 80px;
    height: 41px;
    font-size: 15px;
  }
  @media (max-width: 890px) {
    left: 250px;
  }
`;

const BanButton = styled.div`
  width: 115px;
  height: 56px;
  top: 155px;
  right: 220px;
  background-color: #c2c2c2;
  border-radius: 15px;

  position: absolute;
  font-family: Noto Sans KR;
  font-weight: 900;
  font-size: 20px;
  line-height: 54px;
  text-align: center;
  &:hover {
    background-color: #e6c6c6;
  }
  @media (max-width: 1200px) {
    width: 80px;
    height: 36px;
    line-height: 35px;
    font-size: 15px;
    right: 310px;
  }
  @media (max-width: 1060px) {
    width: 50px;
    left: 200px;
    font-size: 13px;
  }
`;

const UserAlign = styled.div`
  position: absolute;
  background-color: #2c2c2c;

  width: calc(100% - 500px);
  height: 47px; //real size 56
  top: 155px;
  left: 230px;
  border-radius: 15px;

  color: white;
  text-align: left;
  font-family: Noto Sans KR;
  font-weight: 900px;
  font-size: 25px;
  line-height: 36px;
  padding-left: 46px;
  padding-top: 9px;

  @media (max-width: 1200px) {
    left: 120px;
    height: 35px;
    font-size: 20px;
    padding-top: 1px;
    padding-left: 20px;
  }

  @media (max-width: 1060px) {
    font-size: 15px;
    width: 100px;
    padding-top: 0px;
  }
`;

const UserData = [
  {
    name: "서건희",
    id: "1",
  },
  {
    name: "킹남대",
    id: "2",
  },
];

const SelectUser = () => {
  return (
    <WhiteBox>
      <StyledInput
        autoComplete="name"
        name="name"
        placeholder=" USERNAME을 입력해주세요"
      />
      <ButtonStyled>검색</ButtonStyled>
      <UserAlign> {UserData[1].name} </UserAlign>
      <BanButton>BAN</BanButton>
    </WhiteBox>
  );
};

export default SelectUser;
