import React from "react";
import styled from "styled-components";
import Button from "../common/Button";

const WhiteBox = styled.div`
  background-color: white;
  width: calc(100% - 440px);
  height: 840px;
  top: 120px;
  left: 370px;
  position: absolute;
  border: 5px solid #000;
  box-sizing: border-box;
  border-radius: 40px;
`;

const StyledInput = styled.input`
  position: absolute;
  width: calc(100% - 200px);
  height: 51px;
  left: 100px;
  top: 74px;
  background-color: #fff;
  border: 1px solid #000;
  box-sizing: border-box;
  border-radius: 10px;
  @media (max-width: 1000px) {
    font-size: 10px;
    height: 41px;
  }
`;

const ButtonStyled = styled(Button)`
  position: absolute;
  width: 161px;
  height: 51px;
  font-family: CookieRun;
  top: 74px;
  right: 90px;
  color: #fff;
  text-align: center;
  font-size: 30px;
  font-family: Noto Sans KR;
  font-style: normal;
  font-weight: 900;
  font-size: 20px;
  line-height: 29px;

  @media (max-width: 1000px) {
    width: 100px;
    height: 41px;
  }
`;

const UserAlign = styled.div`
  position: absolute;
  text-align: left;
  background-color: #2c2c2c;
  color: white;
  width: 900px;
  height: 56px;
  top: 155px;
`;

const UserData = [
  {
    name: "서건희",
    id: "1",
  },
  {
    name: "킹남대",
    id: "2",
  },
];

const Width = () => {
  var UserWidth = window.innerWidth;
  return UserWidth;
};

const SelectUser = () => {
  return (
    <WhiteBox>
      <StyledInput
        autoComplete="name"
        name="name"
        placeholder=" USERNAME을 입력해주세요"
      />
      <ButtonStyled>검색</ButtonStyled>
      <UserAlign> {UserData[1].name} </UserAlign>
    </WhiteBox>
  );
};

export default SelectUser;
