import React from "react";
import styled from "styled-components";

const UserAlign = styled.div`
  position: absolute;
  background-color: #2c2c2c;

  width: calc(100% - 220px);
  height: 47px; //real size 56
  left: 150px;
  margin-top: 60px;
  border-radius: 15px;
  margin-bottom: 10px;
  color: white;
  text-align: left;
  font-family: Noto Sans KR;
  font-weight: 900px;
  font-size: 25px;
  line-height: 36px;
  padding-left: 46px;
  padding-top: 9px;

  &:first-child {
    margin-top: 0;
  }
  @media (max-width: 1200px) {
    left: 120px;
    height: 35px;
    font-size: 20px;
    padding-top: 1px;
    padding-left: 20px;
  }

  @media (max-width: 1060px) {
    font-size: 15px;
    width: 100px;
    padding-top: 0px;
  }
  @media (max-width: 890px) {
    left: 70px;
  }
`;

const BanButton = styled.div`
  width: 115px;
  height: 56px;

  right: 250px;
  background-color: #c2c2c2;
  border-radius: 10px;
  box-sizing: border-box;

  position: absolute;
  font-family: Noto Sans KR;
  font-weight: 900;
  font-size: 20px;
  line-height: 54px;
  text-align: center;
  &:hover {
    background-color: #e6c6c6;
  }
  @media (max-width: 1200px) {
    width: 80px;
    height: 37px;
    line-height: 35px;
    font-size: 15px;
    right: 310px;
  }
  @media (max-width: 1060px) {
    width: 50px;
    left: 200px;
    height: 35px;
    font-size: 13px;
  }
  @media (max-width: 890px) {
    left: 150px;
  }
`;

const UserListItem = ({ user }) => {
  const { name, id } = user;
  return (
    <div>
      <UserAlign>{name}</UserAlign>
      <BanButton>BAN</BanButton>
    </div>
  );
};

export default UserListItem;
