import React from "react";
import Button from "../common/Button";
import styled from "styled-components";
import { Link } from "react-router-dom";

const AuthFormBlock = styled.div`
  h3 {
    margin: 0;
    font-size: 40px;
    color: white;
    margin-bottom: 4rem;
    position: absolute;
    left: 71px;
    top: 148px;
    margin-bottom: 1rem;
  }
`;

const StyledInput = styled.input`
  background-color: black;
  line-height: 50px;
  font-size: 1rem;
  border: none;
  outline: none;
  border: solid white;
  border-radius: 10px;
  text-indent: 1rem;

  margin-left: 1rem;
  margin-top: 6rem;
  margin-bottom: 2rem;
  width: 85%;

  &:focus {
    color: gray;
  }
  & + & {
    margin-top: 1rem;
  }
`;

const Area = styled.div`
  margin-top: 20px;
  position: absolute;
  top: 150px;
`;

const ButtonMarginTop = styled(Button)`
  margin-top: 1rem;
`;

const AuthForm = () => {
  return (
    <AuthFormBlock>
      <h3>ADMIN</h3>
      <Area>
        <div>
          <StyledInput
            autoComplete="e-mail"
            name="e-mail"
            placeholder=" EMAIL을 입력해주세요"
          />
          <StyledInput
            autoComplete="password"
            name="password"
            placeholder="비밀번호를 입력해주세요"
            type="password"
          />
        </div>
        <ButtonMarginTop fullWidth>로그인</ButtonMarginTop>
      </Area>
    </AuthFormBlock>
  );
};

export default AuthForm;
